mapper.select:
select mr.name, temp.meeting from tb_meeting_room as mr left join ( select place, CONCAT( "[", GROUP_CONCAT( JSON_OBJECT( 'start', DATE_FORMAT(start, '%H:%i'), 'end', DATE_FORMAT(end, '%H:%i'), 'status', status, 'time', ROUND(timestampdiff(MINUTE, CONCAT(date," ",start), CONCAT(date," ",end))/30) ) ), "]" ) as meeting from tb_meeting where type = 2 and status != 2 and date = #{date}
and JSON_CONTAINS(members, cast(#{userId} as char))
group by place ) as temp on mr.name = temp.place where mr.status = 1 limit #{start}, #{length}

mapper.update:
update tb_meeting
set instance_id = #{instanceId}
where uuid = #{uuid}
