mapper.select:
select r.id, u.id, u.`name`, d.dept_name AS deptName, r.content, r.amount, r.anleihen, r.balance, r.type_id AS typeId, r.`status`, DATE_FORMAT(r.create_time,'%Y-%m-%d') AS createTime, IF(r.user_id = #{currentUserId},"true","false") AS mine from tb_reim as r join tb_user as u on r.user_id = u.id join tb_dept as d on u.dept_id = d.id where 1 = 1
AND u.dept_id = #{deptId}
AND r.type_id = #{typeId}
AND r.`status` = #{status}
AND u.id = #{userId}
AND u.name LIKE '%${name}%'
AND r.create_time BETWEEN #{startDate} AND #{endDate}
ORDER BY r.id DESC LIMIT #{start}, #{length}

SELECT COUNT(*)
FROM tb_reim r
JOIN tb_user u ON r.user_id = u.id
JOIN tb_dept d ON d.id = u.dept_id
WHERE 1=1

    AND u.dept_id = #{deptId}


    AND r.type_id = #{typeId}


    AND r.`status` = #{status}


    AND u.id = #{userId}


    AND u.name LIKE '%${name}%'


    AND r.create_time BETWEEN #{startDate} AND #{endDate}

mapper.update:
update tb_reim
set instance_id = #{instanceId}
where id = #{id}
