mapper.select:
select t.id, t.theme, t.desc, u1.name as publisherName, u2.name as executorName, DATE_FORMAT(t.publish_time, '%Y-%m-%d %H:%i') as publishTime, DATE_FORMAT(t.start_time, '%Y-%m-%d %H:%i') as startTime, DATE_FORMAT(t.end_time, '%Y-%m-%d %H:%i') as endTime, t.task_status as taskStatus from tb_task as t inner join tb_user as u1 on t.publisher_id = u1.id inner join tb_user as u2 on t.executor_id = u2.id where 1 = 1
and t.publisher_id = #{publisherId}
and t.executor_id = #{executorId}
and t.task_status = #{taskStatus}
and DATE_FORMAT(t.publish_time,'%Y-%m-%d') = #{publishTime}
order by t.start_time desc limit #{start}, #{length}

select count(*)
from tb_task as t
inner join tb_user as u1 on t.publisher_id = u1.id
inner join tb_user as u2 on t.executor_id = u2.id
where 1 = 1

    and t.publisher_id = #{publisherId}


    and t.executor_id = #{executorId}


    and t.task_status = #{taskStatus}


    and DATE_FORMAT(t.publish_time,'%Y-%m-%d') = #{publishTime}

mapper.update:
update tb_task
set task_status = #{taskStatus}
where id = #{id}

