mapper.select:
select t.id, u.id as userId, u.name, t.title, t.desc, DATE_FORMAT(t.`start`,'%Y-%m-%d %H:%i') AS `start`, DATE_FORMAT(t.`end`,'%Y-%m-%d %H:%i') AS `end`, t.status, t.priority from tb_todo as t join tb_user as u on t.user_id = u.id where t.user_id = #{userId}
and t.status = #{status}
and DATE_FORMAT(t.`start`,'%Y-%m-%d') = DATE_FORMAT(#{date},'%Y-%m-%d')
order by t.priority desc, t.start asc limit #{start}, #{length}

select count(*)
from tb_todo as t
join tb_user as u on t.user_id = u.id
where t.user_id = #{userId}

    and t.status = #{status}


    and DATE_FORMAT(t.`start`,'%Y-%m-%d') = DATE_FORMAT(#{date},'%Y-%m-%d')

mapper.update:
update tb_todo
set `status` = #{status}
where id = #{id} and user_id = #{userId}
