mapper.select:
select distinct u.id, u.name, u.sex, u.tel, u.email, d.dept_name as deptName, u.hiredate, u.root, u.status, ( select group_concat(role_name separator ",") from tb_role where JSON_CONTAINS(u.role, convert(id, char)) ) as roles from tb_user u join tb_role r on JSON_CONTAINS(u.role, convert(r.id, char)) left join tb_dept d on u.dept_id = d.id where 1=1
and name like "%${name}%"
and sex = #{sex}
AND r.role_name= #{role}
AND d.id = #{deptId}
AND u.status =#{status}
order by u.id desc limit #{start}, #{length}

select count(distinct u.id)
from tb_user u join tb_role r on JSON_CONTAINS(u.role, convert(r.id, char))
WHERE 1=1

    AND u.name LIKE "%${name}%"


    AND u.sex=#{sex}


    AND r.role_name=#{role}


    AND u.dept_id=#{deptId}


    AND u.status=#{status}

mapper.delete:
delete from tb_user where id in
#{one}
and root = false

mapper.update:
update tb_user
set photo = #{avatar}
where id = #{id}

mapper.select:
select d.dept_name as deptName, u.name, u.tel, u.email, u.sex from tb_user as u left join tb_dept as d on u.dept_id = d.id where 1 = 1
and u.name like "%${name}%"
and u.dept_id = #{deptId}
and u.sex = #{sex}
order by d.dept_name
